SELECT a.restaurant_id, a.restaurant_name, a.restaurant_description, a.restaurant_address, a.restaurant_phone, a.restaurant_location, b.cuisines_id, b.cuisines_name, a.restaurant_latitude, a.restaurant_longitude, a.restaurant_image, ROUND( 6371 * ACOS( SIN(- 6.597147 * PI() / 180) * SIN(a.restaurant_latitude * PI() / 180) + COS(- 6.597147 * PI() / 180) * COS(a.restaurant_latitude * PI() / 180) * COS( (a.restaurant_longitude * PI() / 180) - (106.806038 * PI() / 180) ) ), 1 ) AS distance FROM restaurant AS a LEFT JOIN cuisines AS b ON a.`restaurant_cuisines_id` = b.`cuisines_id` ORDER BY distance ASC 
